import React, {Component} from 'react';
import './App.css';
import Navbar from './components/Navbar'
import Home from './components/Home'
import Murals from './components/Murals'
import About from './components/About'
import {BrowserRouter, Route, Switch} from 'react-router-dom'
//flesh out the Home component (done)
//flesh out the Mural component (done)
//flesh out the NavBar component (done)
//import them here
//arrange the return to include the <Router> and <Route (with the junk inside here)>

class App extends Component {

  constructor(){
    super()
    this.state = {
      murals: []
    }
  }

  componentDidMount(){
    fetch("http://localhost:3000/murals")
    .then( resp => resp.json() )
    .then( muralData => this.setState({
      murals: muralData
    }))
  }


  render(){
    console.log(this.state.murals)
    return(
      // <BrowserRouter>

        <Switch>
          <Navbar/>
          <Route path='/' component={Home}/>
          <Route path='/murals' render={(props) => (<Murals {...props} murals={this.state.murals}/>)}/>
          <Route path='/about' component={About}/>
        </Switch>

      // </BrowserRouter>
    )
  }
}

export default App;
