import React, { Component } from 'react'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'

class AddMuralForm extends Component {

    constructor() {
        super()
        this.state = {
            mural_title: "",

        }
    }

    renderForm = () => {
        if (this.props.form === "form") {
            return (
                <Form>
                    <Form.Group>
                        <Form.Label>Mural Title</Form.Label>
                        <Form.Control name="mural_title" type="text" placeholder="Mural Title" />
                    </Form.Group>

                    <Form.Group >
                        <Form.Label>Year Installed</Form.Label>
                        <Form.Control name="year_installed" type="text" placeholder="Year Installed" />
                    </Form.Group>

                    <Form.Group >
                        <Form.Label>Description</Form.Label>
                        <Form.Control name="Description" type="text-field" placeholder="Year Installed" />
                    </Form.Group>

                    <Form.Group >
                        <Form.Label>Artist(s)</Form.Label>
                        <Form.Control name="artists" type="text" placeholder="Year Installed" />
                    </Form.Group>

                    <Button variant="test" type="submit" onClick={this.handleSubmit}>
                        Submit
                    </Button>
                </Form>
            )
        }
    }

    handleSubmit = (event) => {
        event.preventDefault()
        console.log("submit button reached")
    }


    render() {
        return (
            <div>
                {this.renderForm()}
            </div>
        )
    }
}

export default AddMuralForm
