import React, {Component} from 'react'
import Mural from '../components/Mural';


class MuralContainer extends Component{


    // once the function below works, pass it as props to <Murals>
    // also might want another map function to pass a single Mural object to render a MuralObj in <aMural>

    allMurals = () => {
        return this.props.murals.map(muralObj => {
            return (
                <div>
                    <div key={muralObj.id}>
                        <h3 onClick={()=> this.props.handleClick(muralObj)}>{muralObj.mural_title}</h3>
                        <p>{muralObj.description}</p>
                    </div>
                    <div>
                        {<Mural mural={this.props.mural} key={muralObj.id} />}
                    </div>
                </div>
                )
        })
    }
    // <BotCard robotClick={this.props.robotClick} bot={robot} key={robot.id} />

    render(){
        console.log("---------", this.props.murals)
        return(
            <div>
                <h1>Mural Container</h1>
                 {this.allMurals()}

            </div>
        )
    }
}

export default MuralContainer
