import React, { Component } from "react";
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'

class EditMural extends Component {

    constructor() {
        super()
        this.state = {
            mural_title: "",
            year_installed: 0,
            description: "",
            artists: ""
        }
    }

    renderEditForm = () => {
        if (this.props.mural.mural != null) {
            return (
                <Form>

                    <Form.Label>Mural Title</Form.Label>
                    <Form.Control type="text" placeholder= {this.props.mural.mural.mural_title} />      

                    <Form.Label>Year Installed</Form.Label>
                    <Form.Control type="text" placeholder= {this.props.mural.mural.year_installed} />

                    <Form.Label>Description</Form.Label>
                    <Form.Control type="text" placeholder= {this.props.mural.mural.description} />

                    <Form.Label>Artists</Form.Label>
                    <Form.Control type="text" placeholder= {this.props.mural.mural.artists} />

                    <Button variant="test" type="submit">Submit</Button>
                </Form>
            )
        }
    }

    render() {

        return (
            <div>{this.renderEditForm()}</div>
        )
    }
}

export default EditMural
